// src/App.js
import React, { useEffect, useMemo, useRef, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import API_URL from "./config";
import "./App.css";

/* polling hook */
function useInterval(callback, delay) {
  const savedRef = useRef();
  useEffect(() => { savedRef.current = callback; }, [callback]);
  useEffect(() => {
    if (delay == null) return;
    const id = setInterval(() => savedRef.current(), delay);
    return () => clearInterval(id);
  }, [delay]);
}

/* Navbar */
// In App.js, update the Navbar component for a more modern look
function Navbar() {
  return (
    <nav className="navbar">
      <div className="nav-left">
        <h2 className="nav-logo">Intervue Poll</h2>
        <div className="nav-links">
          <Link to="/" className="nav-link">Home</Link>
          <Link to="/student" className="nav-link">Student</Link>
          <Link to="/teacher" className="nav-link">Teacher</Link>
        </div>
      </div>
      <div className="nav-right">
        {name && (
          <div className="user-info">
            Signed in as <span className="user-name">{name}</span> ({role})
          </div>
        )}
      </div>
    </nav>
  );
}

// Add a Footer component
function Footer() {
  return (
    <footer className="footer">
      <p>Intervue Poll &copy; {new Date().getFullYear()} - Real-time polling for education</p>
    </footer>
  );
}


export default function App() {
  /* local persisted user info */
  const [role, setRole] = useState(localStorage.getItem("role") || "");
  const [name, setName] = useState(localStorage.getItem("name") || "");

  /* server-driven state (polling updates only these) */
  const [students, setStudents] = useState([]);
  const [polls, setPolls] = useState([]);
  const [activePollId, setActivePollId] = useState(null);
  const [messages, setMessages] = useState([]);

  /* local input states (never reset by polling) */
  const [qText, setQText] = useState("");
  const [options, setOptions] = useState(["", ""]);
  const [duration, setDuration] = useState(60);
  const [chatText, setChatText] = useState("");

  /* derived */
  const activePoll = useMemo(
    () => polls.find(p => String(p._id) === String(activePollId)) || null,
    [polls, activePollId]
  );

  const userHasAnsweredActive = useMemo(() => {
    if (!activePoll || !name) return false;
    return !!(activePoll.answers || []).find(a => a.studentName === name);
  }, [activePoll, name]);

  const secondsLeft = useMemo(() => {
    if (!activePoll) return 0;
    const end = new Date(activePoll.createdAt).getTime() + (Number(activePoll.durationSeconds) || 0) * 1000;
    return Math.max(0, Math.ceil((end - Date.now()) / 1000));
  }, [activePoll]);

  /* fetch server state */
  async function loadState() {
    try {
      const res = await fetch(`${API_URL}/api/state`);
      if (!res.ok) return;
      const data = await res.json();
      // ✅ only update server-driven state
      setStudents(data.students || []);
      setPolls(data.polls || []);
      setActivePollId(data.activePollId || null);
      setMessages(data.messages || []);
    } catch (e) {
      console.warn("loadState error", e);
    }
  }

  useEffect(() => { loadState(); }, []);
  useInterval(loadState, 1500);

  /* actions */
  async function joinAsStudent() {
    if (!name.trim()) return alert("Enter your name");
    try {
      await fetch(`${API_URL}/api/students`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: name.trim() })
      });
    } catch (e) {
      console.warn("join error", e);
    } finally {
      localStorage.setItem("role", "student");
      localStorage.setItem("name", name.trim());
      setRole("student");
    }
  }

  async function createPoll() {
    const clean = options.map(o => o.trim()).filter(Boolean);
    if (!qText.trim() || clean.length < 2) return alert("Enter a question and at least 2 options");
    try {
      await fetch(`${API_URL}/api/polls`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ question: qText.trim(), options: clean, durationSeconds: duration })
      });
      // reset local inputs
      setQText("");
      setOptions(["", ""]);
      setDuration(60);
      loadState();
    } catch (e) {
      console.warn("createPoll error", e);
    }
  }

  async function vote(pollId, optionIndex) {
    try {
      await fetch(`${API_URL}/api/answers`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ pollId, optionIndex, studentName: name })
      });
      loadState();
    } catch (e) {
      console.warn("vote error", e);
    }
  }

  async function kick(n) {
    try {
      await fetch(`${API_URL}/api/kick`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: n })
      });
      loadState();
    } catch (e) {
      console.warn("kick error", e);
    }
  }

  async function sendChat() {
    if (!chatText.trim()) return;
    try {
      await fetch(`${API_URL}/api/messages`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: name || role, text: chatText.trim() })
      });
      setChatText("");
      loadState();
    } catch (e) {
      console.warn("chat error", e);
    }
  }

  /* helpers */
  function ResultBars({ poll }) {
    const total = (poll.counts || []).reduce((a, b) => a + b, 0);
    return (
      <div className="results">
        {poll.options.map((opt, i) => {
          const count = poll.counts?.[i] || 0;
          const pct = total ? Math.round((count / total) * 100) : 0;
          return (
            <div key={i} className="result-row">
              <div className="result-label">{i + 1}. {opt}</div>
              <div className="bar-wrap"><div className="bar" style={{ width: `${pct}%` }} /></div>
              <div className="pct">{pct}%</div>
            </div>
          );
        })}
      </div>
    );
  }

  function ChatPanel() {
    return (
      <div className="chat">
        <div className="chat-list">
          {messages.map((m, i) => <div key={i} className="chat-item"><b>{m.name}:</b> {m.text}</div>)}
        </div>
        <div className="chat-input">
          <input
            type="text"
            className="input"
            value={chatText}
            onChange={e => setChatText(e.target.value)}
            placeholder="Type a message…"
          />
          <button type="button" className="primary" onClick={sendChat}>Send</button>
        </div>
      </div>
    );
  }

  /* pages */
  function HomePage() {
    return (
      <div className="landing">
        <h1>Welcome to Intervue Poll</h1>
        <p className="muted">Use the navigation bar above to join as a student or manage polls as a teacher.</p>
      </div>
    );
  }

  function StudentPage() {
    if (!name) {
      return (
        <div className="panel center-panel">
          <h2>Enter your name</h2>
          <input
            type="text"
            className="input"
            value={name}
            onChange={e => setName(e.target.value)}
            placeholder="e.g. Rahul Bajaj"
          />
          <div className="row" style={{ marginTop: 10 }}>
            <button type="button" className="primary" onClick={joinAsStudent}>Continue</button>
            <button type="button" className="ghost" onClick={() => { setName(""); setRole(""); localStorage.clear(); }}>Cancel</button>
          </div>
        </div>
      );
    }

    if (!activePoll) return <div className="panel"><p className="muted">Wait for the teacher to start a poll…</p><ChatPanel /></div>;
    const disabled = userHasAnsweredActive || secondsLeft <= 0 || activePoll.status !== "active";

    return (
      <div className="panel">
        <div className="poll-head">
          <h3>{activePoll.question}</h3>
          <span className="timer">{secondsLeft}s</span>
        </div>
        <div className="options">
          {activePoll.options.map((opt, i) => (
            <button
              type="button"
              key={i}
              className="option"
              disabled={disabled}
              onClick={() => vote(activePoll._id, i)}
            >
              {opt}
            </button>
          ))}
        </div>
        {disabled && <ResultBars poll={activePoll} />}
        <ChatPanel />
      </div>
    );
  }

  function TeacherPage() {
    return (
      <div className="teacher">
        <div className="panel">
          <h3>Create Poll</h3>
          <textarea
            className="textarea"
            value={qText}
            onChange={e => setQText(e.target.value)}
            placeholder="Enter your question"
          />
          {options.map((opt, i) => (
            <input
              key={i}
              type="text"
              className="input"
              value={opt}
              onChange={e => {
                const arr = [...options]; arr[i] = e.target.value; setOptions(arr);
              }}
              placeholder={`Option ${i + 1}`}
            />
          ))}
          <div className="row">
            <button type="button" className="ghost" onClick={() => setOptions(prev => [...prev, ""])}>+ Add option</button>
            <select className="select" value={duration} onChange={e => setDuration(Number(e.target.value))}>
              <option value={30}>30s</option>
              <option value={60}>60s</option>
              <option value={90}>90s</option>
            </select>
            <button type="button" className="primary" onClick={createPoll}>Ask Question</button>
          </div>
        </div>

        <div className="panel">
          <h3>Live Results</h3>
          {activePoll ? <ResultBars poll={activePoll} /> : <p className="muted">No active poll</p>}
        </div>

        <div className="panel">
          <h3>Participants</h3>
          <ul className="participants-list">
            {students.map(s => (
              <li key={s} className="participant-row">
                <div>{s}</div>
                <div><button type="button" className="ghost" onClick={() => kick(s)}>Kick</button></div>
              </li>
            ))}
          </ul>
        </div>

        <div className="panel">
          <h3>Chat</h3>
          <ChatPanel />
        </div>

        <div className="panel">
          <h3>Poll History</h3>
          {polls.filter(p => p.status !== "active").length === 0 && <div className="muted">No poll history yet</div>}
          {polls.filter(p => p.status !== "active").map((p, i) => (
            <div key={p._id} className="history">
              <b>Q{i + 1}:</b> {p.question}
              <ResultBars poll={p} />
            </div>
          ))}
        </div>
      </div>
    );
  }

  /* render */
  return (
    <Router>
      <Navbar />
      <div className="App">
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/student" element={<StudentPage />} />
          <Route path="/teacher" element={<TeacherPage />} />
        </Routes>
      </div>
      <Footer />
    </Router>
  );
}
