// src/App.js
import React, { useEffect, useMemo, useRef, useState } from "react";
import API_URL from "./config";
import "./App.css";

/** small hook for polling */
function useInterval(callback, delay) {
  const savedRef = useRef();
  useEffect(() => { savedRef.current = callback; }, [callback]);
  useEffect(() => {
    if (delay == null) return;
    const id = setInterval(() => savedRef.current(), delay);
    return () => clearInterval(id);
  }, [delay]);
}

export default function App() {
  // persisted user info
  const [role, setRole] = useState(() => localStorage.getItem("role") || "");
  const [name, setName] = useState(() => localStorage.getItem("name") || "");

  // app state
  const [students, setStudents] = useState([]);
  const [polls, setPolls] = useState([]);
  const [activePollId, setActivePollId] = useState(null);
  const [messages, setMessages] = useState([]);

  // teacher inputs
  const [qText, setQText] = useState("");
  const [options, setOptions] = useState(["", ""]);
  const [duration, setDuration] = useState(60);

  // chat input
  const [chatText, setChatText] = useState("");

  // sync localStorage when role/name change
  useEffect(() => { if (role) localStorage.setItem("role", role); else localStorage.removeItem("role"); }, [role]);
  useEffect(() => { if (name) localStorage.setItem("name", name); else localStorage.removeItem("name"); }, [name]);

  // derived states
  const activePoll = useMemo(() => polls.find(p => String(p._id) === String(activePollId)) || null, [polls, activePollId]);

  const userHasAnsweredActive = useMemo(() => {
    if (!activePoll || !name) return false;
    return !!(activePoll.answers || []).find(a => a.studentName === name);
  }, [activePoll, name]);

  const secondsLeft = useMemo(() => {
    if (!activePoll) return 0;
    const end = new Date(activePoll.createdAt).getTime() + (Number(activePoll.durationSeconds) || 0) * 1000;
    return Math.max(0, Math.ceil((end - Date.now()) / 1000));
  }, [activePoll, /* refresh tick*/ pollTimestamp()]);

  // small hack to trigger re-evaluation of secondsLeft every second
  function pollTimestamp() { return Math.floor(Date.now() / 1000); }

  // Load state from server
  async function loadState() {
    try {
      const res = await fetch(`${API_URL}/api/state`);
      if (!res.ok) throw new Error("state fetch failed");
      const data = await res.json();
      setStudents(data.students || []);
      setPolls(data.polls || []);
      setActivePollId(data.activePollId || null);
      setMessages(data.messages || []);
    } catch (e) {
      // offline / server not available: keep whatever we have
      console.warn("loadState failed", e);
    }
  }

  useEffect(() => { loadState(); }, []);
  useInterval(loadState, 1200); // poll every 1.2s for near-live updates
  useInterval(() => {}, 1000); // keep a second tick for secondsLeft calculation

  // Role selection
  function chooseRole(r) {
    setRole(r);
    localStorage.setItem("role", r);
  }

  // Student join (persist even if backend fails)
  async function joinAsStudent() {
    const trimmed = (name || "").trim();
    if (!trimmed) return alert("Please enter your name");
    try {
      const res = await fetch(`${API_URL}/api/students`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: trimmed })
      });
      if (!res.ok) {
        const err = await res.json().catch(() => ({}));
        console.warn("students POST:", err);
      }
    } catch (e) {
      console.warn("Network error while adding student:", e);
    } finally {
      setName(trimmed);
      setRole("student");
      localStorage.setItem("name", trimmed);
      localStorage.setItem("role", "student");
      await loadState();
    }
  }

  // Teacher: create poll
  async function createPoll() {
    const question = (qText || "").trim();
    const cleanedOptions = (options || []).map(o => o.trim()).filter(Boolean);
    if (!question) return alert("Please add a question");
    if (cleanedOptions.length < 2) return alert("At least two options are required");
    try {
      const res = await fetch(`${API_URL}/api/polls`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ question, options: cleanedOptions, durationSeconds: Number(duration) || 60 })
      });
      if (res.status === 409) {
        const data = await res.json().catch(()=>({}));
        return alert(data.error || "Cannot create poll now");
      }
      if (!res.ok) {
        const data = await res.json().catch(()=>({}));
        return alert(data.error || "Failed to create poll");
      }
      setQText("");
      setOptions(["",""]);
      setDuration(60);
      await loadState();
    } catch (e) {
      console.error("createPoll error", e);
      alert("Network error while creating poll");
    }
  }

  // Student: vote
  async function vote(pollId, optionIndex) {
    if (!name) return alert("Please enter your name before voting");
    try {
      const res = await fetch(`${API_URL}/api/answers`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ pollId, optionIndex, studentName: name })
      });
      if (!res.ok) {
        const data = await res.json().catch(()=>({}));
        return alert(data.error || "Failed to submit answer");
      }
      await loadState();
    } catch (e) {
      console.warn("vote network error", e);
      alert("Network error while submitting answer");
    }
  }

  // Kick student (teacher)
  async function kick(n) {
    if (!window.confirm(`Kick ${n} from session?`)) return;
    try {
      const res = await fetch(`${API_URL}/api/kick`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: n })
      });
      if (!res.ok) {
        const data = await res.json().catch(()=>({}));
        console.warn("kick error", data);
        alert("Failed to kick student");
      }
      await loadState();
    } catch (e) {
      console.warn("kick network error", e);
      alert("Network error while kicking student");
    }
  }

  // Chat
  async function sendChat() {
    const txt = (chatText || "").trim();
    if (!txt) return;
    try {
      const res = await fetch(`${API_URL}/api/messages`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: name || (role || "User"), text: txt })
      });
      if (!res.ok) {
        console.warn("chat post failed");
      }
      setChatText("");
      await loadState();
    } catch (e) {
      console.warn("chat network", e);
      setChatText("");
    }
  }

  // Add/remove poll options UI
  function addOption() { setOptions(prev => [...prev, ""]); }
  function removeOption(i) { setOptions(prev => prev.filter((_, idx) => idx !== i)); }
  function updateOption(i, v) { setOptions(prev => prev.map((p, idx) => idx === i ? v : p)); }

  // Logout / reset
  function resetAll() {
    if (!window.confirm("Clear role and name and return to landing?")) return;
    setRole("");
    setName("");
    localStorage.removeItem("role");
    localStorage.removeItem("name");
  }

  // UI Components
  function Header() {
    return (
      <header className="topbar">
        <div className="brand">
          <div className="logo" />
          <div className="title">Intervue Poll</div>
        </div>
        <div className="top-actions">
          {name && <div className="small muted">Signed in as <strong>{name}</strong></div>}
          {role && <button type="button" className="ghost" onClick={resetAll}>Back</button>}
        </div>
      </header>
    );
  }

  function Landing() {
    return (
      <div className="center-screen">
        <h1>Welcome to the Live Polling System</h1>
        <p className="muted">Please select the role that best describes you</p>
        <div className="role-cards">
          <button type="button" className="role-card" onClick={() => chooseRole("student")}>
            <h3>I’m a Student</h3>
            <p className="muted">Submit answers and view live poll results.</p>
          </button>
          <button type="button" className="role-card" onClick={() => chooseRole("teacher")}>
            <h3>I’m a Teacher</h3>
            <p className="muted">Create polls and monitor responses in real-time.</p>
          </button>
        </div>
      </div>
    );
  }

  function StudentEnterName() {
    return (
      <div className="panel center-panel">
        <h2>Let’s Get Started</h2>
        <label className="label">Enter your name</label>
        <input
          className="input"
          value={name}
          onChange={e => setName(e.target.value)}
          placeholder="e.g., Rahul Bajaj"
        />
        <div className="row">
          <button type="button" className="primary" onClick={joinAsStudent}>Continue</button>
          <button type="button" className="ghost" onClick={() => { setRole(""); localStorage.removeItem("role"); }}>Cancel</button>
        </div>
      </div>
    );
  }

  function StudentWaiting() {
    return (
      <div className="grid-two">
        <div className="panel">
          <h3>Waiting</h3>
          <p className="muted">Wait for the teacher to ask questions…</p>
          <div className="participants small muted">{students.length} participant(s)</div>
        </div>
        <div className="panel">
          <ChatPanel />
        </div>
      </div>
    );
  }

  function StudentQuestion() {
    if (!activePoll) return <StudentWaiting />;

    const disabled = userHasAnsweredActive || activePoll.status !== "active" || secondsLeft <= 0;

    return (
      <div className="grid-two">
        <div className="panel poll-panel">
          <div className="poll-head">
            <div className="q-title">Question</div>
            <div className="timer">{String(Math.floor(secondsLeft / 60)).padStart(2, "0")}:{String(secondsLeft % 60).padStart(2,"0")}</div>
          </div>
          <h3 className="q-text">{activePoll.question}</h3>

          <div className="options-wrap">
            {activePoll.options.map((opt, i) => (
              <button
                key={i}
                type="button"
                className="option-btn"
                disabled={disabled}
                onClick={() => vote(activePoll._id, i)}
              >
                <div className="opt-left"><span className="opt-index">{i + 1}</span><span className="opt-text">{opt}</span></div>
              </button>
            ))}
          </div>

          {disabled && (
            <>
              <div className="divider" />
              <h4>Results</h4>
              <ResultBars poll={activePoll} />
              <div className="muted small">Wait for the teacher to ask a new question…</div>
            </>
          )}
        </div>

        <div className="panel">
          <ChatPanel />
        </div>
      </div>
    );
  }

  function TeacherDashboard() {
    const canAskNew = !activePoll || activePoll.status !== "active" || secondsLeft === 0 || (activePoll.answers?.length || 0) >= students.length;

    return (
      <div className="teacher-grid">
        <div className="panel">
          <h3>Create a Poll</h3>
          <label className="label">Enter your question (max 100 chars)</label>
          <textarea className="textarea" value={qText} onChange={e => setQText(e.target.value)} maxLength={100} placeholder="Which planet is known as the Red Planet?" />
          <div className="muted small">{qText.length}/100</div>

          <label className="label">Options</label>
          <div className="options-list">
            {options.map((v, i) => (
              <div className="option-row" key={i}>
                <div className="opt-num">{i + 1}</div>
                <input className="input" value={v} onChange={e => updateOption(i, e.target.value)} placeholder={`Option ${i + 1}`} />
                {options.length > 2 ? (
                  <button type="button" className="ghost danger" onClick={() => removeOption(i)}>Remove</button>
                ) : null}
              </div>
            ))}
            <button type="button" className="ghost" onClick={addOption}>+ Add More option</button>
          </div>

          <label className="label">Time Limit (seconds)</label>
          <select className="select" value={duration} onChange={e => setDuration(Number(e.target.value))}>
            <option value={30}>30</option>
            <option value={45}>45</option>
            <option value={60}>60</option>
            <option value={90}>90</option>
          </select>

          <div className="row" style={{ marginTop: 12 }}>
            <button type="button" className="primary" onClick={createPoll} disabled={!canAskNew}>Ask Question</button>
            {!canAskNew && <div className="muted small">You can ask new question only after the current poll ends or all students answer.</div>}
          </div>
        </div>

        <div className="panel">
          <h3>Live Results</h3>
          {activePoll ? (
            <>
              <div className="poll-head">
                <div className="q-title">Question</div>
                <div className="timer">{String(Math.floor(secondsLeft / 60)).padStart(2, "0")}:{String(secondsLeft % 60).padStart(2,"0")}</div>
              </div>
              <div className="q-text">{activePoll.question}</div>
              <ResultBars poll={activePoll} />
              <div className="muted small">{(activePoll.answers?.length || 0)}/{students.length} answered</div>
            </>
          ) : (
            <div className="muted">No active poll</div>
          )}

          <div className="divider" style={{ margin: "12px 0" }} />
          <h4>Participants</h4>
          <ul className="participants-list">
            {students.map(s => (
              <li key={s} className="participant-row">
                <div>{s}</div>
                <div>
                  <button type="button" className="ghost" onClick={() => kick(s)}>Kick out</button>
                </div>
              </li>
            ))}
          </ul>
        </div>

        <div className="panel">
          <h3>Chat</h3>
          <ChatPanel compact />
        </div>

        <div className="panel">
          <h3>Poll History</h3>
          <div className="history">
            {polls.filter(p => p.status !== "active").length === 0 && <div className="muted">No poll history yet</div>}
            {polls.filter(p => p.status !== "active").map((p, idx) => (
              <div key={p._id} className="history-item">
                <div className="history-title">Question {idx + 1}</div>
                <div className="history-q">{p.question}</div>
                <ResultBars poll={p} />
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  function ResultBars({ poll }) {
    const total = (poll.counts || []).reduce((a, b) => a + b, 0);
    return (
      <div className="results">
        {poll.options.map((opt, i) => {
          const count = poll.counts?.[i] || 0;
          const pct = total ? Math.round((count / total) * 100) : 0;
          return (
            <div className="result-row" key={i}>
              <div className="result-left">
                <div className="res-index">{i + 1}</div>
                <div className="res-label">{opt}</div>
              </div>
              <div className="res-bar-wrap">
                <div className="res-bar" style={{ width: `${pct}%` }} />
              </div>
              <div className="res-pct">{pct}%</div>
            </div>
          );
        })}
      </div>
    );
  }

  function ChatPanel({ compact = false }) {
    return (
      <div className={`chat-panel ${compact ? "compact" : ""}`}>
        <div className="chat-list">
          {messages.map((m, i) => (
            <div className="chat-item" key={i}>
              <div className="chat-meta"><strong>{m.name}</strong> <span className="muted small">{new Date(m.timestamp).toLocaleTimeString()}</span></div>
              <div>{m.text}</div>
            </div>
          ))}
        </div>
        <div className="chat-input">
          <input className="input" value={chatText} onChange={e => setChatText(e.target.value)} placeholder="Type a message…" onKeyDown={e => { if (e.key === "Enter") sendChat(); }} />
          <button type="button" className="primary" onClick={sendChat}>Send</button>
        </div>
      </div>
    );
  }

  // Main render
  return (
    <div className="App">
      <Header />
      <main className="container">
        {!role && <Landing />}
        {role === "student" && !name && <StudentEnterName />}
        {role === "student" && name && (activePoll ? <StudentQuestion /> : <StudentWaiting />)}
        {role === "teacher" && <TeacherDashboard />}
      </main>
    </div>
  );
}
